import React from 'react';
import './drinks.csv';
  
function GetData(){
    fetch('./drinks.csv')
    function convertToArray(fileName = "drinks.csv", separator = ";") {

        document.getComponentById(fileName, function(fileContent){
            
            var resultArray = [];
            var textArray = fileContent.split(/(\r\n|\n|\r)/gm);
            for (var i = 0; i < textArray.length; i++) {
        
                if (textArray[i].length > 1) {
                    var elementArray = textArray[i].split(separator);
                    elementArray.splice(elementArray.length - 1, 1);
                    resultArray.push(elementArray);
                } 
            } 
            console.log(resultArray);
            return(resultArray);
        }); 
    }
}

   
export default GetData;
